import * as React from "react";
import './menu.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Home from '../components/home';
//import Resume from '../components/resume';
//import Portfolio from '../components/portfolio';
//import Contact from '../components/contact';

import ParticleComponent from "./particlesComponent";

class Menu extends React.Component {

    componentDidMount() {
        window.addEventListener("scroll", this.resizeHeaderOnScroll);
    }

    resizeHeaderOnScroll() {
        const distanceY = window.pageYOffset || document.documentElement.scrollTop,
            shrinkOn = 80,
            navbarEl = document.getElementById("navbar"),
            logoEl = document.getElementById("logo");

        if (distanceY > shrinkOn) {
            navbarEl.style.padding = "30px 10px";
            logoEl.style.fontSize = "25px";
        } else {
            navbarEl.style.padding = "80px 10px";
            logoEl.style.fontSize = "35px";
        }
    }

    render() {
        return (
            <Router>
                <div id="navbar">
                    <div style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%"
                    }}>
                        <ParticleComponent />
                        <div style={{
                            position: "absolute",
                            top: 0,
                            left: 0,
                            width: "100%",
                            height: "100%"
                        }}>
                            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                            <a href="#default" id="logo">MTM</a>
                            <ul id="navbar-right" className="navbar-nav mr-auto">
                                <li><Link to={'/'} className="nav-link"> Home </Link></li>
                                {/*<li><Link to={'/resume'} className="nav-link">Resume</Link></li>
                                <li><Link to={'/portfolio'} className="nav-link">Portfolio</Link></li>
                                <li><Link to={'/contact'} className="nav-link">Contact</Link></li>*/}
                            </ul>
                            </nav>
                        </div>
                    </div>
                    <Switch>
                        <Route exact path='/' component={Home} />
                        {/*<Route path='/resume' component={Resume} /> 
                        <Route path='/portfolio' component={Portfolio} /> 
                        <Route path='/contact' component={Contact} /> */}
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default Menu;
