import * as React from "react";
import './menu.css';
import { BrowserRouter as Router, Switch, Route, Link, NavLink } from 'react-router-dom';
import Home from '../components/home';
import Resume from '../components/resume';
import Portfolio from '../components/portfolio';
import Contact from '../components/contact';

import ParticleComponent from "./particlesComponent";

export default class menu extends React.Component {

    componentDidMount() {
        window.addEventListener("scroll", this.resizeHeaderOnScroll);
    }

    resizeHeaderOnScroll() {
        const distanceY = window.pageYOffset || document.documentElement.scrollTop,
            shrinkOn = 80,
            navbarEl = document.getElementById("navbar"),
            logoEl = document.getElementById("logo");

        if (distanceY > shrinkOn) {
            navbarEl.style.padding = "30px 10px";
            logoEl.style.fontSize = "25px";
        } else {
            navbarEl.style.padding = "80px 10px";
            logoEl.style.fontSize = "35px";
        }
    }

    render() {
        return (
            <Router>
                <div id="navbar">
                    <div style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%", 
                        height: "100%"
                    }}>
                        <ParticleComponent />
                        <div style={{
                            position: "absolute",
                            top: 0,
                            left: 0,
                            width: "100%", 
                            height: "100%"
                        }}>
                            <Link to={'/'} className="nav-link" id="logo">MTM</Link>
                            <div id="navbar-right">
                                <NavLink to={'/'} className="nav-link"> Home </NavLink>
                                <NavLink to={'/resume'} className="nav-link">Resume</NavLink>
                                <NavLink to={'/portfolio'} className="nav-link">Portfolio</NavLink>
                                <NavLink to={'/contact'} className="nav-link">Contact</NavLink>
                            </div>
                        </div>
                    </div>
                </div>
                <Switch>
                        <Route exact path='/' component={Home} /> 
                        <Route path='/resume' component={Resume} /> 
                        <Route path='/portfolio' component={Portfolio} /> 
                        <Route path='/contact' component={Contact} />
                </Switch>
            </Router>
        );
    }
}
