import * as React from "react";
import './menu.css';

export default class menu extends React.Component {

  componentDidMount() {
    window.addEventListener("scroll", this.resizeHeaderOnScroll);
  }

  resizeHeaderOnScroll() {
    const distanceY = window.pageYOffset || document.documentElement.scrollTop,
        shrinkOn = 80,
        navbarEl = document.getElementById("navbar"),
        logoEl = document.getElementById("logo");

    if (distanceY > shrinkOn) {
        navbarEl.style.padding = "30px 10px";
        logoEl.style.fontSize = "25px";
    } else {
        navbarEl.style.padding = "80px 10px";
        logoEl.style.fontSize = "35px";
    }
  }

  render() {
    return (
        <div id="navbar">
            <a href="#default" id="logo"><i class="favicon.ico"></i>MTM</a>
            <div id="navbar-right">
                <a href="#home">Home</a>
                <a href="#resume">Resume</a>
                <a href="#portfolio">Portfolio</a>
                <a href="#contact">Contact</a>
            </div>
        </div>
    );
  }
}
